import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Router, Switch, Route, Link } from 'react-router-dom';
import CartIcon from './components/svg/shopping-cart-solid.svg';
import './App.css';

import Login from './components/Login';
import Register from './components/Register';
import Home from './components/Home';
import Profile from './components/Profile';
import BoardUser from './components/BoardUser';
import BoardModerator from './components/BoardModerator';
import BoardAdmin from './components/BoardAdmin';

import { logout } from './actions/auth.action';
import { clearMessage } from './actions/message.action';

import { history } from './helpers/history';
import Shoes from './components/Shoes';
import Details from './components/Details';
import Clothes from './components/Clothes';
import Bags from './components/Bags';
import Accesories from './components/Accesories';
import Cart from './components/Cart';
import Products from './components/Products';
import Product from './components/Product';
import Landing from './components/Landing';

const App = () => {
	const [ showModeratorBoard, setShowModeratorBoard ] = useState(false);
	const [ showAdminBoard, setShowAdminBoard ] = useState(false);

	const { user: currentUser } = useSelector((state) => state.auth);
	const dispatch = useDispatch();

	useEffect(
		() => {
			history.listen((location) => {
				dispatch(clearMessage()); // clear message when changing location
			});
		},
		[ dispatch ]
	);

	useEffect(
		() => {
			if (currentUser) {
				setShowModeratorBoard(currentUser.roles.includes('ROLE_MODERATOR'));
				setShowAdminBoard(currentUser.roles.includes('ROLE_ADMIN'));
			}
		},
		[ currentUser ]
	);

	const logOut = () => {
		dispatch(logout());
	};
	return (
		<Router history={history}>
			<div>
				<nav className="navbar navbar-expand navbar-transparent bg-transparent">
					<Link to={'/'} className="navbar-brand">
						<h1>Nike Shop</h1>
					</Link>
					<div className="navbar-nav mr-auto">
						<li className="nav-item">
							<Link to={'/home'} className="nav-link">
								Accueil
							</Link>
						</li>
						<li className="nav-item">
							<Link to={'/chaussures'} className="nav-link">
								Chaussures
							</Link>
						</li>
						<li className="nav-item">
							<Link to={'/vetements'} className="nav-link">
								VÃªtements
							</Link>
						</li>
						<li className="nav-item">
							<Link to={'/sacs'} className="nav-link">
								Sacs
							</Link>
						</li>
						<li className="nav-item">
							<Link to={'/accesoires'} className="nav-link">
								Accesoires
							</Link>
						</li>
						{showModeratorBoard && (
							<li className="nav-item">
								<Link to={'/mod'} className="nav-link">
									Moderator Board
								</Link>
							</li>
						)}

						{showAdminBoard && (
							<li className="nav-item">
								<Link to={'/admin'} className="nav-link">
									Admin Board
								</Link>
							</li>
						)}

						{currentUser && (
							<li className="nav-item">
								<Link to={'/user'} className="nav-link">
									User
								</Link>
							</li>
						)}
					</div>

					{currentUser ? (
						<div className="navbar-nav ml-auto">
							<li className="nav-item">
								<Link to={'/profile'} className="nav-link">
									{currentUser.username}
								</Link>
							</li>
							<li className="nav-item">
								<a href="/login" className="nav-link" onClick={logOut}>
									LogOut
								</a>
							</li>
						</div>
					) : (
						<div className="navbar-nav ml-auto">
							<li className="nav-item">
								<Link to={'/login'} className="nav-link">
									Login
								</Link>
							</li>

							<li className="nav-item">
								<Link to={'/register'} className="nav-link">
									Sign Up
								</Link>
							</li>
							<div className="nav-cart">
								<span className="test">1</span>
								<Link to="/panier">
									<img className="test" src={CartIcon} alt="" width="20" />
								</Link>
							</div>
						</div>
					)}
				</nav>
			</div>
			<Switch>
				<Route exact path={[ '/', '/home' ]} component={Landing} />
				 <Route exact path="/login" component={Login} />
				 <Route exact path="/register" component={Register} />
				 <Route exact path="/profile" component={Profile} />
				 <Route exact path="/user" component={BoardUser} />
				 <Route exact path="/mod" component={BoardModerator} />
				 <Route exact path="/admin" component={BoardAdmin} />
				 <Route exact path="/chaussures" component={Shoes} />
				 <Route exact path="/chaussures/:id" component={Product} />
				 <Route exact path="/vetements" component={Clothes} />
				 <Route exact path="/sacs" component={Products} />
				 <Route exact path="/accesoires" component={Accesories} />
				 <Route exact path="/panier" component={Cart} />
			</Switch>
		</Router>
	);
};

export default App;
