import React, { Component, useState } from 'react';
import { Link } from 'react-router-dom';
// import Colors from './Colors';
// import '../css/Details.css';
// import '../css/Cart.css';

const Cart = (cart) => {
	const products = [
		{
			_id: '1',
			title: 'Nike Shoes 01',
			src: 'https://i.pinimg.com/originals/ff/d4/cb/ffd4cbee3b78cb17a0c9d41cd9119b71.jpg',
			description: 'Nike shoes 01',
			content:
				'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
			price: 23,
			colors: [ 'red', 'black', 'crimson', 'teal' ],
			count: 1
		},
		{
			_id: '2',
			title: 'Nike Shoes 02',
			src:
				'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSrTyUz9JmEuxbUk5nhYPIDTySya1ysJvkWzQ&usqp=CAU',
			description: 'Nike shoes 02',
			content:
				'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
			price: 19,
			colors: [ 'red', 'crimson', 'teal' ],
			count: 1
		},
		{
			_id: '3',
			title: 'Nike Shoes 03',
			src: 'https://i.pinimg.com/originals/14/31/09/143109457e56f0791d15a35d49ad9bf4.jpg',
			description: 'Nike shoes 03',
			content:
				'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
			price: 50,
			colors: [ 'lightblue', 'white', 'crimson', 'teal' ],
			count: 1
		},
		{
			_id: '4',
			title: 'Nike Shoes 04',
			src: 'https://cdn.def-shop.com/pic530x705/nike-t-shirt-bleu-738034.jpg',
			description: 'Nike shoes 04',
			content:
				'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
			price: 15,
			colors: [ 'orange', 'black', 'crimson', 'teal' ],
			count: 1
		},
		{
			_id: '5',
			title: 'Nike Shoes 05',
			src:
				'https://static.nike.com/a/images/c_limit,w_318,f_auto/t_product_v1/387c0bd9-57d6-4285-9a7b-e2ea84c15a81/sweat-a-capuche-pour-petit-gn5gF1.jpg',
			description: 'Nike shoes 05',
			content:
				'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
			price: 10,
			colors: [ 'orange', 'black', 'crimson', 'teal' ],
			count: 1
		},
		{
			_id: '6',
			title: 'Nike Shoes 06',
			src:
				'https://cdn.sarenza.net/_img/productsv4/0000258189/0000258189_472551_09_504x690.jpg?202009151543&v=20180830155342',
			description: 'Nike shoes 06',
			content:
				'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
			price: 17,
			colors: [ 'orange', 'black', 'crimson', 'teal' ],
			count: 1
		}
	];

	// const [ cart, setCart ] = useState([]);
	const [ total, setTotal ] = useState('');

	const addCart = (id) => {
		const check = cart.every((item) => {
			return item._id !== id;
		});
		if (check) {
			const data = products.filter((product) => {
				return product._id === id;
			});

		} else {
			alert('Le produit a été ajouté au panier.');
		}
	};

	const reduction = (id) => {
		cart.forEach((item) => {
			if (item._id === id) {
				item.count === 1 ? (item.count = 1) : (item.count -= 1);
			}
		});

		getTotal();
	};

	const increase = (id) => {
		const { cart } = this.state;
		cart.forEach((item) => {
			if (item._id === id) {
				item.count += 1;
			}
		});

		getTotal();
	};

	const removeProduct = (id) => {
		if (window.confirm('Voulez-vous vraiment supprimer cet article?')) {
			const { cart } = this.state;
			cart.forEach((item, index) => {
				if (item._id === id) {
					cart.splice(index, 1);
				}
			});

			getTotal();
		}
	};

	const getTotal = () => {
		const res = cart.reduce((prev, item) => {
			return prev + item.price * item.count;
		}, 0);
		setTotal({ total: res });
	};

	return (
		<div>
			{cart.length === 0 ? (
				<h2 style={{ textAlign: 'center' }}>Aucun produit dans le panier</h2>
			) : (
				cart.map((item) => (
					<div className="details cart" key={item._id}>
						<img src={item.src} alt="" />
						<div className="box">
							<div className="row">
								<h2>{item.title}</h2>
								<span>{item.price * item.count}€</span>
							</div>
							{/* <Colors colors={item.colors} /> */}
							<p>{item.description}</p>
							<p>{item.content}</p>
							<div className="amount">
								<button className="count" onClick={() => reduction(item._id)}>
									{' '}
									-{' '}
								</button>
								<span>{item.count}</span>
								<button className="count" onClick={() => increase(item._id)}>
									{' '}
									+{' '}
								</button>
							</div>
						</div>
						<div className="delete" onClick={() => removeProduct(item._id)}>
							X
						</div>
					</div>
				))
			)}
			<div className="total">
				<Link to="/payment">Paiement</Link>
				<h3>Total:{total}€</h3>
			</div>
		</div>
	);
};

export default Cart;
