import React, { useState } from 'react';
import { Container, Col, Row } from 'reactstrap';
import './landing.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleLeft, faAngleRight, faArrowRight } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

import DiscoverBag from './DiscoverBag';
import DiscoverClothe from './DiscoverClothe';
import DiscoverAccesory from './DiscoverAccesory';
import DiscoverShoes from './DiscoverShoes';

const Discover = () => {
	const description = [
		{
			gamme: "d'accessoires",
			text: 'text the text',
			link: 'accessoires'
		},
		{
			gamme: 'de sacs',
			text: 'text the text',
			link: 'sacs'
		},
		{
			gamme: 'de vêtements',
			text: 'text the text',
			link: 'vetements'
		},
		{
			gamme: 'de chaussures',
			text: 'text the text',
			link: 'chaussures'
		}
    ];

	const [ activeIndex, setActiveIndex ] = useState(0);

	const previous = () => {
		let index = activeIndex;
    let length = description.length;

if(index < 1) {
      index = length - 1;
    }
    else {
      index--;
    }

      setActiveIndex(index)
	};
	const next = () => {
		const nextIndex = activeIndex === description.length - 1 ? 0 : activeIndex + 1;
		setActiveIndex(nextIndex);
	};

	return (
		<Col className="section" lg="6">
			{description.map((desc, index) => (
				<div className={index === activeIndex ? 'active' : 'inactive'} key={index}>
					<h1> Découvrez notre gamme {desc.gamme}</h1>
					<p className="text">
						Sed ut perspiciatis unde omnis iste natus error sit voluptatem accusantium doloremque
						laudantium, totam rem aperiam, eaque ipsa quae ab illo inventore veritatis et quasi architecto
						beatae vitae dicta sunt explicabo. Nemo enim ipsam voluptatem quia voluptas sit aspernatur aut
						laudantium, totam rem aperiam, eaque ipsa quae ab illo inventore veritatis et quasi architecto
						beatae vitae dicta sunt explicabo. Nemo enim ipsam voluptatem quia voluptas sit aspernatur aut
					</p>
					<button onClick={previous()}>
						<FontAwesomeIcon icon={faAngleLeft} />
					</button>
					<button>
						<FontAwesomeIcon icon={faAngleRight} />
					</button>
					<Link to={`/${desc.link}`}className="arrowRight">
						<p>
							Voir plus <FontAwesomeIcon icon={faArrowRight} />
						</p>
					</Link>
				</div>
			))}
		</Col>
	);
};

export default Discover;
