import React, { PropTypes } from 'react';
import { Link } from 'react-router-dom';
// import './account.css';

export function SubmitButton ({emailError, firstNameError, lastNameError}) {
  if (
    emailError ||
    firstNameError ||
    lastNameError
  ) {
    return (
      <button
        action='submit'
        disabled={ "disabled" }
        className='linkButton disabledButton'
        role='button'>Not ready</button>
    )
  } else {
    return (
      <button
        action='submit'
        className='linkButton'
        role='button'>Update Profile</button>
    )
  }
}

export default function Profile ({
	updateUser,
	message,
	updateFirstName,
	updateLastName,
	updateEmail,
	validateEmail,
	validateFirstName,
	validateLastName,
	emailError,
	firstNameError,
	lastNameError,
	email,
	firstName,
	lastName,
	user,
	isFetching,
	error
}) {
  function handleSubmit(e) {
    e.preventDefault();

    return updateUser({
      email: email,
      first_name: firstName,
      last_name: lastName
    });
  }

  return isFetching
  ? <h3>Loading ...</h3>
  : Object.keys(user).length === 0 || error.length > 0
    ? <h3>{ error }</h3>
    : <div className="accountContainer">
        <h3>Your Account</h3>

        <form onSubmit={ handleSubmit } className='editForm'>
          <label className='formLabel'>
            <span className="labeltitle">Email</span><br />
            <span className='errorMessage'>{ emailError }</span>
            <input
              id='updateEmail'
              name='updateEmail'
              className='formInput'
              type='email'
              placeholder='Email ...'
              defaultValue={ user.get('email') }
              onChange={ (e) => updateEmail(e.target.value)}
              onBlur={ (e) => validateEmail(e.target.value) }
              required={ true } />
          </label>

          <label className='formLabel'>
            <span className="labeltitle">First Name</span><br />
            <span className='errorMessage'>{ firstNameError }</span>
            <input
              id='updateFirstName'
              name='updateFirstName'
              className='formInput'
              type='text'
              placeholder='First Name ...'
              defaultValue={ user.get('first_name') }
              onChange={ (e) => updateFirstName(e.target.value)}
              onBlur={ (e) => validateFirstName(e.target.value) }
              required={ true } />
          </label>

          <label className='formLabel'>
            <span className="labeltitle">Last Name</span><br />
            <span className='errorMessage'>{ lastNameError }</span>
            <input
              id='updateLastName'
              name='updateLastName'
              className='formInput'
              type='text'
              placeholder='Last Name ...'
              defaultValue={ user.get('last_name') }
              onChange={ (e) => updateLastName(e.target.value)}
              onBlur={ (e) => validateLastName(e.target.value) }
              required={ true } />
          </label>
          <p className="updateMessage">{ message }</p>

          <br />
          <SubmitButton
            emailError={ emailError }
            lastNameError={ lastNameError }
            firstNameError={ firstNameError } />
        </form>

        <br />
        <Link to="/account/receipts" role="link" className="receiptLink">See Receipts</Link>
      </div>;
}

Profile.propTypes = {
  updateUser: PropTypes.func.isRequired,
  message: PropTypes.string.isRequired,
  error: PropTypes.string.isRequired,
  isFetching: PropTypes.bool.isRequired,
  user: PropTypes.object.isRequired,
  updateFirstName: PropTypes.func.isRequired,
  updateLastName: PropTypes.func.isRequired,
  updateEmail: PropTypes.func.isRequired,
  validateEmail: PropTypes.func.isRequired,
  validateFirstName: PropTypes.func.isRequired,
  validateLastName: PropTypes.func.isRequired,
  firstNameError: PropTypes.string.isRequired,
  lastNameError: PropTypes.string.isRequired,
  emailError: PropTypes.string.isRequired,
  email: PropTypes.string.isRequired,
  firstName: PropTypes.string.isRequired,
  lastName: PropTypes.string.isRequired,
};
