import {
	FETCHING_CART,
	ADD_TO_CART_SUCCESS,
	CLEAR_ERROR_MESSAGE,
	FETCHING_CART_SUCCESS,
	FETCHING_CART_FAILURE,
	PAY_TOTAL_FAILURE,
	PAID_TOTAL_SUCCESSFULLY
} from '../actions/types';

const initialState = {

	error: '',
	isFetching: false,
	message: ''
};

// Reducers
export default function cart(state = {cartItems: []}, action) {
	const { type, payload } = action;
	switch (type) {
		case FETCHING_CART:
			return {
				...state, 
				isFetching: true
			};

		case ADD_TO_CART_SUCCESS:
			const item = action.payload;
      const product = state.cartItems.find(x => x.product === item.product);
      if (product) {
        return {
          cartItems:
            state.cartItems.map(x => x.product === product.product ? item : x)
        };
      }
      return { cartItems: [...state.cartItems, item] };

		case CLEAR_ERROR_MESSAGE:
			return {
				...state,
				message: '',
				error: ''
			};

		case FETCHING_CART_FAILURE:
			return {
				...state,
				isFetching: false,
				error: action.error
			};

		case FETCHING_CART_SUCCESS:
			return {
				...state,
				isFetching: false,
				error: '',
				cart: action.cart
			};

		case PAY_TOTAL_FAILURE:
			return {
				...state,
				isFetching: false,
				error: action.error
			};

		case PAID_TOTAL_SUCCESSFULLY:
			return {
				...state,
				isFetching: false,
				error: ''
			};

		default:
			return state;
	}
}
