import {
	UPDATE_YOUR_EMAIL,
	YOUR_EMAIL_ERROR,
	RESET_PASS_SUCCESS,
	RESET_PASS_FAILURE,
	CLEAR_MESSAGE
} from '../actions/types';

const initialState = {
	error: '',
	message: '',
	email: '',
	emailError: ''
};

export default function (state = initialState, action) {
	const { type, payload } = action;
	switch (type) {
		case UPDATE_YOUR_EMAIL:
			return {
				...state,
				email: payload.email,
				emailError: ''
            };

		case YOUR_EMAIL_ERROR:
			return {
				...state,
				emailError: payload.emailError
			};

		case RESET_PASS_SUCCESS:
			return {
				...state,
				message: payload.message,
				error: ''
			};

		case CLEAR_MESSAGE:
			return {
				...state,
				message: '',
				error: ''
			};

		case RESET_PASS_FAILURE:
			return {
				...state,
				error: payload.error,
				message: ''
			};

		default:
			return state;
	}
}
