import {
	FETCHING_PURCHASED_PRODUCT,
	FETCHING_PURCHASED_PRODUCT_FAILURE,
	FETCHING_PURCHASED_PRODUCT_SUCCESS
} from '../actions/types';

const initialState = Map({
	product: {},
	error: '',
	isFetching: false
});

export default function purchased(state = initialState, action) {
	const { type, payload } = action;
	switch (type) {
		case FETCHING_PURCHASED_PRODUCT:
			return {
				isFetching: true
			};

		case FETCHING_PURCHASED_PRODUCT_FAILURE:
			return {
				isFetching: false,
				error: action.error
			};

		case FETCHING_PURCHASED_PRODUCT_SUCCESS:
			return {
				isFetching: false,
				error: '',
				product: action.product
			};

		default:
			return state;
	}
}
