    const addCart = (id) =>{
        const check = cart.every(item =>{
            return item._id !== id
        })
        if(check){
            const data = products.filter(product =>{
                return product._id === id
            })
            setCart({cart: [...cart,...data]})
        }else{
            alert("Le produit a été ajouté au panier.")
        }
