        if(window.confirm("Voulez-vous vraiment supprimer cet article?")){
            const {cart} = this.state;
            cart.forEach((item, index) =>{
                if(item._id === id){
                    cart.splice(index, 1)
                }
            })
            setCart({cart: cart});
            getTotal();
        }

    };

    const getTotal = ()=>{
        const res = cart.reduce((prev, item) => {
            return prev + (item.price * item.count);
        },0)
        setTotal({total: res})
    };
	return (
		<div id="product">
			{products.map((product) => (
				<div className="card">
