import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './Products.css';

const Products = ({ products, isFetching, error }) => {
	return (
		<div id="product">
			{products.map((product) => (
				<div className="card">
					<Link to={`/chaussures/${product._id}`}>
						<img src={product.src} alt="" />
					</Link>
					<div className="content">
						<h3>
							<Link to={`/chaussures/${product._id}`}>{product.title}</Link>
						</h3>
						<span>{product.price}â‚¬</span>
						<p>{product.description}</p>
						<button>Ajouter au panier</button>
						{/* <button onClick={() => addCart(product._id)}>Ajouter au panier</button> */}
					</div>
				</div>
			))}
		</div>
	);
};

export default Products;
