import { FETCHING_RECEIPTS, FETCHING_RECEIPTS_SUCCESS, FETCHING_RECEIPTS_FAILURE } from '../actions/types';

const initialState = {
	receipts: [],
	error: '',
	isFetching: false
};

export default function receipts(state = initialState, action) {
	const { type, payload } = action;
	switch (type) {
		case FETCHING_RECEIPTS:
			return {
                ...state,
				isFetching: true
			}; 

		case FETCHING_RECEIPTS_FAILURE:
			return {
                ...state,
				isFetching: false,
				error: payload.error
			};

		case FETCHING_RECEIPTS_SUCCESS:
			return {
                ...state,
				isFetching: false,
				error: '',
				receipts: payload.receipts
			};

		default:
			return state;
	}
}
