import {
	UPDATE_REVIEW_BODY,
	UPDATE_REVIEW_RATING,
	SUBMISSION_REVIEW_SUCCESS,
	SUBMISSION_EREVIEW_ERROR,
	DELETION_REVIEW_SUCCESS,
	DELETION_REVIEW_ERROR
} from './actions/types';

const initialState = {
	body: '',
	rating: '',
	error: ''
};

// reducer
export default function reviewFormReducer(state = initialState, action) {
    const {type, payload} = action
	switch (type) {
		case UPDATE_REVIEW_BODY:
			return {
                ...state,
				body: action.body
			};

		case UPDATE_REVIEW_RATING:
			return {
                ...state,
				rating: action.rating
			};

		case SUBMISSION_REVIEW_SUCCESS:
			return {
				...state,
			};

		case SUBMISSION_EREVIEW_ERROR:
			return {
                ...state,
				error: action.error
			};

		case DELETION_REVIEW_SUCCESS:
			return {
                ...state,
				error: ''
			};

		case DELETION_REVIEW_ERROR:
			return {
                ...state,
				error: action.error
			};

		default:
			return state;
	}
}
