import {
	UPDATE_ACCOUNT_FIRST_NAME,
	UPDATE_ACCOUNT_LAST_NAME,
	UPDATE_ACCOUNT_EMAIL,
	ACCOUNT_FIRST_NAME_ERROR,
	ACCOUNT_LAST_NAME_ERROR,
	ACCOUNT_EMAIL_ERROR,
	FETCHING_ACCOUNT_USER,
	FETCHING_ACCOUNT_USER_SUCCESS,
	FETCHING_ACCOUNT_USER_FAILURE,
	UPDATED_MESSAGE,
	CLEAR_UPDATED_MESSAGE
} from '../actions/types';

const initialState = {
	user: {},
	error: '',
	isFetching: false,
	firstName: '',
	lastName: '',
	email: '',
	firstNameError: '',
	lastNameError: '',
	emailError: '',
	message: ''
};

export default function account(state = initialState, action) {
	const { type, payload } = action;
	switch (type) {
		// Update
		case UPDATE_ACCOUNT_FIRST_NAME:
			return {
				...state,
				firstName: payload.firstName,
				firstNameError: ''
			};

		case UPDATE_ACCOUNT_LAST_NAME:
			return {
				...state,
				lastName: payload.lastName,
				lastNameError: ''
			};

		case UPDATE_ACCOUNT_EMAIL:
			return {
				...state,
				email: payload.email,
				emailError: ''
			};

		case UPDATED_MESSAGE:
			return {
				...state,
				message: payload.message,
				error: ''
			};

		case CLEAR_UPDATED_MESSAGE:
			return {
				...state,
				message: '',
				error: ''
			};

		// Error
		case ACCOUNT_FIRST_NAME_ERROR:
			return {
				...state,
				firstNameError: payload.firstNameError
			};

		case ACCOUNT_LAST_NAME_ERROR:
			return {
				...state,
				lastNameError: payload.lastNameError
			};

		case ACCOUNT_EMAIL_ERROR:
			return {
				...state,
				emailError: payload.emailError
			};

		// Fetch
		case FETCHING_ACCOUNT_USER:
			return {
				...state,
				isFetching: true
			};

		case FETCHING_ACCOUNT_USER_FAILURE:
			return {
				...state,
				isFetching: false,
				error: payload.error
			};

		case FETCHING_ACCOUNT_USER_SUCCESS:
			return {
				...state,
				isFetching: false,
				error: '',
				user: payload.user
			};

		default:
			return state;
	}
}
